chart.options:
legend: {
  show: false
},
chart: {
  height: 350,
  type: 'treemap'
},
title: {
  text: 'Treemap with Color scale'
},
dataLabels: {
  enabled: true,
  style: {
    fontSize: '12px',
  },
  formatter: function(text, op) {
    return [text, op.value]
  },
  offsetY: -4
},
plotOptions: {
  treemap: {
    enableShades: true,
    shadeIntensity: 0.5,
    reverseNegativeShade: true,
    colorScale: {
      ranges: [
        {
          from: -6,
          to: 0,
          color: '#CD363A'
        },
        {
          from: 0.001,
          to: 6,
          color: '#52B12C'
        }
      ]
    }
  }
}
